function init(){for(var e=0;e<localStorage.length;e++){var n=localStorage.key(e);"note"===n.substring(0,6)&&addStickiesToPage(localStorage.getItem(n))}}function addStickiesToPage(e){var n=document.getElementById("note-"),t=document.createElement("li"),a=document.createElement("span");a.setAttribute("class","note"),a.innerHTML=e,t.appendChild(a),n.appendChild(t)}function showSpinner(){$(".taskHandler").show()}function hideSpinner(){$(".taskHandler").hide()}window.onload=init;